var newIP=args.newIP.trim();if(newIP){logger.log("Trying to add IP "+newIP+" to the node.");if(!document.hasAspect("ax:ip")){document.addAspect("ax:ip");logger.log("Aspect ax:ip added to the node")}var listOfIPs=document.properties["ax:ipName"]||[];if(listOfIPs.indexOf(newIP)===-1){listOfIPs.push(newIP);document.properties["ax:ipName"]=listOfIPs;try{document.save();logger.log("Succesfully added IP "+newIP+" to the node")}catch(e){logger.log("Error saving changes to the node: "+e.message)}}else{logger.log("IP already found in Node's IPs")}}else{logger.log("No IP was given as argument")};